#!/usr/bin/env bash
# =============================================================================
# Theme: Everforest
# Variant: Light
# Description: Soft, paper-like light theme
# Source: https://github.com/sainnhe/everforest
# =============================================================================

declare -gA THEME_COLORS=(
    # Status Bar
    [statusbar-bg]="#efebd4"
    [statusbar-fg]="#5c6a72"

    # Session
    [session-bg]="#8da101"
    [session-fg]="#fdf6e3"
    [session-prefix-bg]="#dfa000"
    [session-copy-bg]="#35a77c"

    # Windows (base colors - variants auto-generated)
    [window-active-base]="#df69ba"
    [window-inactive-base]="#e6e2cc"

    # Pane Borders
    [pane-border-active]="#df69ba"
    [pane-border-inactive]="#e6e2cc"

    # Health States (base colors - variants auto-generated)
    [ok-base]="#829181"
    [good-base]="#8da101"
    [info-base]="#35a77c"
    [warning-base]="#dfa000"
    [error-base]="#f85552"
    [disabled-base]="#939f91"

    # Messages
    [message-bg]="#efebd4"
    [message-fg]="#5c6a72"

)
