#!/usr/bin/env bash
# =============================================================================
# Theme: Rose Pine
# Variant: Dawn
# Description: Light variant with natural pine vibes
# Source: https://rosepinetheme.com/
# =============================================================================

declare -gA THEME_COLORS=(
    # Status Bar
    [statusbar-bg]="#f2e9e1"
    [statusbar-fg]="#575279"

    # Session
    [session-bg]="#56949f"
    [session-fg]="#faf4ed"
    [session-prefix-bg]="#ea9d34"
    [session-copy-bg]="#286983"

    # Windows (base colors - variants auto-generated)
    [window-active-base]="#907aa9"
    [window-inactive-base]="#dfdad9"

    # Pane Borders
    [pane-border-active]="#907aa9"
    [pane-border-inactive]="#dfdad9"

    # Health States (base colors - variants auto-generated)
    [ok-base]="#797593"
    [good-base]="#56949f"
    [info-base]="#286983"
    [warning-base]="#ea9d34"
    [error-base]="#b4637a"
    [disabled-base]="#9893a5"

    # Messages
    [message-bg]="#f2e9e1"
    [message-fg]="#575279"

)
