#!/usr/bin/env bash
# =============================================================================
# Theme: Rose Pine
# Variant: Moon
# Description: Darker variant with natural pine vibes
# Source: https://rosepinetheme.com/
# =============================================================================

declare -gA THEME_COLORS=(
    # Status Bar
    [statusbar-bg]="#393552"
    [statusbar-fg]="#e0def4"

    # Session
    [session-bg]="#9ccfd8"
    [session-fg]="#232136"
    [session-prefix-bg]="#f6c177"
    [session-copy-bg]="#3e8fb0"

    # Windows (base colors - variants auto-generated)
    [window-active-base]="#c4a7e7"
    [window-inactive-base]="#393552"

    # Pane Borders
    [pane-border-active]="#c4a7e7"
    [pane-border-inactive]="#393552"

    # Health States (base colors - variants auto-generated)
    [ok-base]="#2a273f"
    [good-base]="#9ccfd8"
    [info-base]="#3e8fb0"
    [warning-base]="#f6c177"
    [error-base]="#eb6f92"
    [disabled-base]="#6e6a86"

    # Messages
    [message-bg]="#393552"
    [message-fg]="#e0def4"

)
